 onDispose {
 onExit()
 }
 }

 ExpensesTodayScreenContent(
 uiState = uiState,
 goToHistoryScreen = goToHistoryScreen,
 goToAddTransactionScreen = goToAddExpenseScreen,
 goToEditExpenseScreen = goToEditExpenseScreen
 )
}

