private fun ExpensesTodayScreenContent(
 uiState: ExpensesTodayScreenState,
 goToHistoryScreen: ()-> Unit,
 goToAddTransactionScreen: () -> Unit,
 goToEditExpenseScreen: (Int) -> Unit
) {
